import React, { createContext, useReducer } from 'react';
import axios from 'axios';
import taskReducer from '../reducers/taskReducer';

// Create context
export const TaskContext = createContext();

// Initial state
const initialState = {
  tasks: [],
  currentTask: null,
  loading: true,
  error: null
};

// Provider component
export const TaskProvider = ({ children }) => {
  const [state, dispatch] = useReducer(taskReducer, initialState);

  // Get all tasks
  const getTasks = async () => {
    try {
      const res = await axios.get('/api/tasks');

      dispatch({
        type: 'GET_TASKS',
        payload: res.data.data
      });
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
    }
  };

  // Get task by ID
  const getTask = async (id) => {
    try {
      const res = await axios.get(`/api/tasks/${id}`);

      dispatch({
        type: 'GET_TASK',
        payload: res.data.data
      });
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
    }
  };

  // Create task
  const createTask = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    try {
      const res = await axios.post('/api/tasks', formData, config);

      dispatch({
        type: 'CREATE_TASK',
        payload: res.data.data
      });

      return res.data.data;
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
      throw err;
    }
  };

  // Update task
  const updateTask = async (id, formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    try {
      const res = await axios.put(`/api/tasks/${id}`, formData, config);

      dispatch({
        type: 'UPDATE_TASK',
        payload: res.data.data
      });

      return res.data.data;
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
      throw err;
    }
  };

  // Update task status
  const updateTaskStatus = async (id, status) => {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    try {
      const res = await axios.put(`/api/tasks/${id}/status`, { status }, config);

      dispatch({
        type: 'UPDATE_TASK',
        payload: res.data.data
      });

      return res.data.data;
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
      throw err;
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`/api/tasks/${id}`);

      dispatch({
        type: 'DELETE_TASK',
        payload: id
      });
    } catch (err) {
      dispatch({
        type: 'TASK_ERROR',
        payload: err.response.data.message
      });
    }
  };

  // Clear current task
  const clearCurrentTask = () => {
    dispatch({ type: 'CLEAR_CURRENT_TASK' });
  };

  // Clear errors
  const clearErrors = () => {
    dispatch({ type: 'CLEAR_ERRORS' });
  };

  return (
    <TaskContext.Provider
      value={{
        tasks: state.tasks,
        currentTask: state.currentTask,
        loading: state.loading,
        error: state.error,
        getTasks,
        getTask,
        createTask,
        updateTask,
        updateTaskStatus,
        deleteTask,
        clearCurrentTask,
        clearErrors
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};
